mapper.select:
select *, category.name as categoryName from setmeal left join category on setmeal.category_id = category.id


        AND setmeal.name LIKE CONCAT('%', #{name}, '%')


        AND setmeal.category_id = #{categoryId}


        AND setmeal.status = #{status}


order by setmeal.update_time desc

select count(id) from setmeal


        and status = #{status}


        and category_id = #{categoryId}
